import{_ as e,c as r,o,ag as a}from"./chunks/framework.CI8cTucl.js";const c=JSON.parse('{"title":"About LWT Export Templates for \\"Flexible Exports\\"","description":"","frontmatter":{},"headers":[],"relativePath":"reference/export-templates.md","filePath":"reference/export-templates.md"}'),d={name:"reference/export-templates.md"};function s(n,t,l,x,i,p){return o(),r("div",null,[...t[0]||(t[0]=[a('<h1 id="about-lwt-export-templates-for-flexible-exports" tabindex="-1">About LWT Export Templates for &quot;Flexible Exports&quot; <a class="header-anchor" href="#about-lwt-export-templates-for-flexible-exports" aria-label="Permalink to &quot;About LWT Export Templates for &quot;Flexible Exports&quot;&quot;">â€‹</a></h1><p>An export template consists of a string of characters. Some parts of this string are <strong>placeholders</strong> (beginning with <strong>&quot;%&quot;, &quot;$&quot; or &quot;\\&quot;</strong>) that are <strong>replaced</strong> by the actual term data, <strong>see the following table</strong>. For each term (word or expression), that has been selected for export, the placeholders of the export template will be replaced by the term data and the string will be written to the export file.</p><p><strong>A template must end with</strong> either <strong>&quot;\\n&quot;</strong> (UNIX, Mac) or <strong>&quot;\\r\\n&quot;</strong> (Windows). <strong>If you omit this, the whole export will be one single line!</strong></p><p>If the export template is <strong>empty, no terms of this language</strong> will be exported.</p><table tabindex="0"><thead><tr><th>Placeholders</th><th>Placeholders replaced by ...</th></tr></thead><tbody><tr><td><strong>%...</strong></td><td><strong>Raw Text</strong></td></tr><tr><td>%w</td><td>Term (Word/Expression) - as raw text.</td></tr><tr><td>%t</td><td>Translation - as raw text.</td></tr><tr><td>%s</td><td>Sentence, curly braces removed - as raw text.</td></tr><tr><td>%c</td><td>The sentence, but the &quot;{xxx}&quot; parts are replaced by &quot;[...]&quot; (close review question) - as raw text.</td></tr><tr><td>%d</td><td>The sentence, but the &quot;{xxx}&quot; parts are replaced by &quot;[xxx]&quot; (close review solution) - as raw text.</td></tr><tr><td>%r</td><td>Romanization - as raw text.</td></tr><tr><td>%a</td><td>Status (1..5, 98, 99) - as raw text.</td></tr><tr><td>%k</td><td>Term in lowercase (key) - as raw text.</td></tr><tr><td>%z</td><td>Tag List - as raw text.</td></tr><tr><td>%l</td><td>Language - as raw text.</td></tr><tr><td>%n</td><td>Word Number in LWT (key in table &quot;words&quot;) - as raw text.</td></tr><tr><td>%%</td><td>Just one percent sign &quot;%&quot;.</td></tr><tr><td><strong>$...</strong></td><td><strong>HTML Text.</strong> HTML special characters are escaped:&lt; = &amp;lt; / &gt; = &amp;gt; / &amp; = &amp;amp; / &quot; = &amp;quot;</td></tr><tr><td>$w</td><td>Term (Word/Expression) - as HTML text.</td></tr><tr><td>$t</td><td>Translation - as HTML text.</td></tr><tr><td>$s</td><td>Sentence, curly braces removed - as HTML text.</td></tr><tr><td>$c</td><td>The sentence, but the &quot;{xxx}&quot; parts are replaced by &quot;[...]&quot; (close review question) - as HTML text.</td></tr><tr><td>$d</td><td>The sentence, but the &quot;{xxx}&quot; parts are replaced by &quot;[xxx]&quot; (close review solution) - as HTML text.</td></tr><tr><td>$x</td><td>The sentence in Anki2 close review notation: the &quot;{xxx}&quot; parts are replaced by &quot;{{c1::xxx}}&quot; - as HTML text.</td></tr><tr><td>$y</td><td>The sentence in Anki2 close review notation, with translation: the &quot;{xxx}&quot; parts are replaced by &quot;{{c1::xxx::translation}}&quot; - as HTML text.</td></tr><tr><td>$r</td><td>Romanization - as HTML text.</td></tr><tr><td>$k</td><td>Term in lowercase (key) - as HTML text.</td></tr><tr><td>$z</td><td>Tag List - as HTML text.</td></tr><tr><td>$l</td><td>Language - as HTML text.</td></tr><tr><td>$$</td><td>Just one dollar sign &quot;$&quot;.</td></tr><tr><td><strong>\\...</strong></td><td><strong>Special Characters</strong></td></tr><tr><td>\\t</td><td>TAB character (HEX 9).</td></tr><tr><td>\\n</td><td>NEWLINE character (HEX 10).</td></tr><tr><td>\\r</td><td>CARRIAGE RETURN character (HEX 13).</td></tr><tr><td>\\\\</td><td>Just one backslash &quot;&quot;.</td></tr></tbody></table>',5)])])}const h=e(d,[["render",s]]);export{c as __pageData,h as default};
